		ld	a,l		add	20h		ld	l,a		ret	c		ld	a,h		sub	8		ld	h,a		retStr_buff_pixel:		db 	0,0,0,0,0,0,0,0		db 	0,0,0,0,0,0,0,0Str_flg_end:		db 	0Str_mask_bit:		dw 	0Str_addr_symbol:		dw 	0Str_addr_work:		dw 	0Str_addr_load:		db	'LOADING...      ',0Str_addr_text:		db	'               ‚…’ ‚‘… ‹’…‹ "ZX-SPECTRUM"!!!     '		db	'‘“‘’ 12 ‹…’ ‘„‹‘ —’ ’ ‡€‡’'		db	' „‹ ‘… ‘™…‘’‚€. —… …„‘’€‚‹ '		db	'ƒ€“ "E-TUNES", „… ’‰ ‡€‘’‚‚€€'		db	' ‘ ’…€ "SAM COUPE". ƒ€€ …„€‡€—…€'		db	' „‹ ‘‹“‚€ “‡›€‹›• ‡‚…„…‰ '		db 	'…„€’€ "E-TRACKER" —……‡ ‡‚“‚“ €’“ '		db	'"ZXM-SOUNDCARD", €‡€’€“ € ‘‚… ‘•…› '		db	'"TSFM" ’ ƒ“› NEDO PC (WWW.NEDOPC.COM)  '		db	'‘•…› ‚‹—… ‡‚“‚ƒ —€ SAA1099 ’ '		db	'VELESOFT (WWW.VELESOFT.SPECCY.CZ/SAA1099-CZ.HTM).'		db	' ‘‘’‚… ’€ ƒ€€  ‡€„…‰‘’‚“…’ ’“ —€‘’ '		db	'€’›, ’€ ‡€…’ — SAA1099.  '		db	'‚ ’ ‚›“‘… ƒ€› ‚‹—… 20'		db      ' “‡›€‹›• ‡‚…„…‰. ……•„  ‘‹…„“™…“ '		db	'‡‚…„… ‘“™…‘’‚‹…’‘  ™ ‹€‚'		db	' "SPACE", ‚›•„ ‚ TR-DOS  ™ ‹€‚ "BREAK"'		db	'  €•  „€, ‡€›‹ …„‘’€‚’‘ - MICK, … † €‰’'		db	'  E-MAIL: MICKLAB@MAIL.RU  € ‘€‰’… ZX.PK.RU'		db 	'  ƒ€…. €’€, €€‹‡€’, ƒ›‚€’…‹'		db	' “‡›€‹›•'		db	' „“‹…‰ ›‹ ‘„€› ‘ € "SAM COUPE", ‘’€‹…'		db	' “†…  …   :)) ‚‘… ’ •‡‰‘’‚ €‘€ € €‘‘…‹……'		db	' SJASMPLUS.            ’€„– €“ ‚…’‚'		db	' ‚ ™… ‚…’ ‚‘… ‘…’“‘’€, ›‚, €‘’™  ‚‡†'		db	' “„“™. ‘€‘™… CHRV  VELESOFT ‡€ • ’‚—…‘’‚. ' 		db	'’„…‹›‰ …‘…’ ‚‹€„…‹–€ ’…€ ' 		db	'"ZXM-PHOENIX" (‘’€’ €—€’› €’› €„ ‚’‰ ‚…‘…‰).'		db	'  ƒ… ‹€ƒ„€™… €€€ ‡€ ’ —’  ’€‰ …‘’'		db	' (, ‘’€’ ‹€„€’…‹ €’› "ZXM-SOUNDCARD" '		db	'  †…’ ‘‹“€’ ‘… “‡›€‹›… ‡‚…„…. :) € ‘’€‹›…'		db	' ’‹ ƒ“’ €‹„€’ ‡€ …ƒ“™…‰ ‘’‰  ›ƒ€…'		db	' €€‹‡€’€).  “ ‚‘…, ‡€’€‹‘ ‘—’ …„, €  …“'		db	' ‡€’, € ™€’‘. €, €.......                   '		db	'              *2010* CODE AND GRAPHICS BY MICK              '		db	'                                             ',0           		Str_addr_font:		incbin "dbfnt.fnt";		.end